import { Component, OnInit } from '@angular/core';
import { FormGroup,FormControl ,Validators} from '@angular/forms';
import { EmployeeService } from '../employee.service';

@Component({
  selector: 'app-employee-list',
  templateUrl: './employee-list.component.html',
  styleUrls: ['./employee-list.component.css']
})
export class EmployeeListComponent implements OnInit{
genders = ["male","female"]

employeeForm !:FormGroup;
employeeList:any;


  constructor(private emplyoyeeservice:EmployeeService){}

  ngOnInit(): void {
   this.getemployeeList()

    this.employeeForm= new FormGroup({
      email: new FormControl('',Validators.required),
      name: new FormControl('',Validators.required),
      dob: new FormControl('',Validators.required),
      department: new FormControl('',Validators.required),
      gender: new FormControl('',Validators.required),

    });

  }



  addEmployee(){
    console.log(this.employeeForm.value)
    this.emplyoyeeservice.addEmployee(this.employeeForm.value)
      this.employeeList();

  }

  getemployeeList(){
     this.emplyoyeeservice.getEmployeeList().subscribe(res=>{
      console.log(res);
      this.employeeList= res;
     })


  }


  deleteemployee(id:number){
    this.emplyoyeeservice.deleteEmployee(id)
      this.employeeList();

  }

  editEmplyoyee(id:number){

    this.emplyoyeeservice.singalEmployeeDetail(id).subscribe(res=>{
      console.log(res);
      this.employeeForm.patchValue(res);
    })



  }


  updateEmplyoyee(id:number,emplyoyee:any){
    this.emplyoyeeservice.updateEmployee(id,emplyoyee)

  }
}
